window.addEventListener("load",()=>{const darkModeToggle=document.querySelector('#switch-theme');darkModeToggle.parentNode.classList.add("active")});document.addEventListener('DOMContentLoaded',()=>{const THEME_KEY="theme";const html=document.documentElement;const toggle=document.querySelector("#switch-theme");function applyTheme(mode){html.setAttribute("data-theme",mode);toggle.checked=mode==="dark";localStorage.setItem(THEME_KEY,mode)}
const storedTheme=localStorage.getItem(THEME_KEY);const initialTheme=storedTheme==="dark"?"dark":"light";applyTheme(initialTheme);toggle.addEventListener("change",()=>{const newTheme=toggle.checked?"dark":"light";applyTheme(newTheme)});const hamburger=document.querySelector(".hamburger");const nav=document.querySelector(".navbar");hamburger.addEventListener("click",()=>nav.classList.toggle("active"));if(window.innerWidth<768){nav.classList.add("is-mobile")}
window.addEventListener('resize',()=>{nav.classList.remove("active");if(window.innerWidth<768){nav.classList.add("is-mobile")}})})